INSERT INTO `item` (`id`, `title`, `excerpt`, `content`, `image`, `category`, `feature`, `views`, `created_at`, `updated_at`, `author`) VALUES
(0, 'Haaland làm rung chuyển thế giới bóng đá', 'Trong một thoáng chốc, sân Etihad như vỡ òa. Cả thế giới bóng đá như nín thở khi chứng kiến pha vô-lê bằng gót chân không tưởng của Erling Haaland.', 'Bóng bay vút vào góc lưới một cách hoàn hảo, khiến thủ môn Sparta Prague chỉ biết đứng nhìn. Đó không chỉ là một bàn thắng, mà còn là một tác phẩm nghệ thuật, một khoảnh khắc người ta sẽ còn nhắc đến trong nhiều năm tới. Điều gì đã khiến một cầu thủ lại có thể thực hiện những pha bóng điên rồ đến vậy?\r\n\r\nThách thức mới của Haaland\r\nNhững con số thống kê dường như không còn quan trọng khi nói đến Haaland nữa. Ai cũng biết anh sẽ ghi vô số bàn thắng, nhưng điều khiến người ta phải sững sờ chính là cách cầu thủ này làm điều đó.\r\n\r\nBàn thắng \"điên rồ\" của Haaland trong chiến thắng 5-0 trước Sparta Prague tại League phase Champions League mùa 2024/25 rạng sáng 24/10 (giờ Hà Nội) mang lại cảm giác như khi ta còn là đứa trẻ say mê bóng đá, gợi nhắc về những gì khiến môn thể thao này trở nên đặc biệt, và vì sao những tiền đạo mang sức mạnh phi thường luôn khơi dậy cảm xúc mãnh liệt hơn bất kỳ ai khác.\r\n\r\n\r\n<img src=\"https://photo.znews.vn/w1920/Uploaded/bpivpawv/2024_10_24/Haaland2_2_.jpg\">\r\nHaaland khiến tất cả ngỡ ngàng với pha làm bàn \"điên rồ\".\r\nMột lần nữa, Haaland thách thức các định luật vật lý. Khoảng 10 mét từ khung thành Sparta Prague, anh không hướng mặt về phía lưới đối phương. Đường tạt bóng của Savinho nguy hiểm nhưng dường như không phải để tiền đạo có thể dứt điểm. Một lựa chọn thông minh là chuyền lại cho đồng đội. Nhưng Haaland không phải là người thích chọn cách an toàn.\r\n\r\nSau nhiều lần bị ngăn chặn bởi các pha bóng bổng, Haaland quyết định thử vận may với cú vô-lê. Nếu dùng chân phải để xoay người dứt điểm thì cũng đã là quá táo bạo. Thế nhưng, anh chọn cách khác: thực hiện cú vô-lê bằng gót chân trái để đưa bóng vào góc lưới. Một cú vô-lê bằng gót chân, nếu có thể gọi như vậy.\r\n\r\n\"Đó không phải là điều bình thường với một con người\", HLV Pep Guardiola của Man City nói.\r\n\r\nCả sân Etihad như nín thở khi pha quay chậm hiện lên trên màn hình lớn. Bernardo Silva gãi đầu thắc mắc. Haaland chỉ nhún vai và cười tươi, như thể đó là công việc hàng ngày của anh.\r\n\r\nCó lẽ không nhiều - nếu không muốn nói là không có - cầu thủ nào trên thế giới dám thử điều đó, chứ chưa nói đến việc thành công. Và đây cũng không phải là may mắn: Haaland từng thực hiện pha bóng tương tự trước Borussia Dortmund cách đây hai năm.\r\n\r\nĐó chính là những gì anh mang lại, một cầu thủ có thể chia rẽ ý kiến của những người luôn tìm cách chỉ trích hơn là tận hưởng sự thăng hoa cảm xúc mà bản thân khơi gợi. Haaland thực hiện những điều mà người hâm mộ Manchester City sẽ nhớ mãi trong nhiều thập kỷ tới, và đó chính là di sản của anh.\r\n\r\n\"Không nói nên lời\", tiền vệ Matheus Nunes thốt lên. \"Nếu tôi thử làm điều đó chắc chắn tôi sẽ rách háng\". Phil Foden hài hước nói thêm. \"Anh ấy đúng là quái vật, phải không?\"\r\n\r\nBản tuyên ngôn từ Haaland\r\nPha vô-lê bằng gót chân của Haaland không chỉ đơn thuần là một bàn thắng, mà còn là một tuyên ngôn về sức mạnh và sự sáng tạo của bóng đá. Đó là khoảnh khắc thời gian như ngừng lại, khi mọi ánh mắt đều đổ dồn vào một điểm duy nhất: trái bóng đang bay trên không trung.\r\n\r\nTrong tích tắc ấy, Haaland biến mình thành một nghệ sĩ, vẽ nên một bức tranh tuyệt đẹp bằng đôi chân của mình. Pha bóng này không chỉ khiến các cổ động viên Manchester City vỡ òa trong sung sướng mà còn khiến cả thế giới bóng đá phải trầm trồ thán phục. Đó là một pha bóng mà ngay cả những cầu thủ kỹ thuật bậc cao như Zinedine Zidane hay Ronaldinho cũng phải ngả mũ thán phục.\r\n\r\n\r\n<img src=\"https://photo.znews.vn/w1920/Uploaded/bpivpawv/2024_10_24/Haaland_2_.jpg\">\r\nVới Haaland, không gì là không thể.\r\nSiêu phẩm của Haaland không chỉ gợi nhớ về quá khứ mà còn khẳng định thêm đẳng cấp và sự độc đáo trong lối chơi của tiền đạo người Na Uy. Người luôn khiến cả thế giới phải ngả mũ trước những pha bóng đầy ma thuật và sức mạnh phi thường.\r\n\r\nHaaland rời sân sớm để giữ sức, kết thúc trận đấu với 44 bàn thắng sau 42 lần ra sân tại Champions League, cân bằng thành tích của huyền thoại Didier Drogba. Dù có lúc bị chỉ trích vì không ghi bàn trong ba trận liên tiếp tại Premier League, nhưng nụ cười của Haaland vẫn luôn nở trên môi.\r\n\r\n\"Điều quan trọng là cậu ấy luôn vui vẻ, mỉm cười\", Guardiola chia sẻ. \"Các hậu vệ luôn bám chặt, nhưng rõ ràng vẫn chưa đủ để ngăn cản cậu ấy\".\r\n\r\nVới 7 điểm trong tay sau 3 lượt trận thuộc League phase Champions League, Man City đang nắm chắc trong tay tấm vé vào vòng loại trực tiếp. Theo tính toán của \"siêu máy tính\" Opta, chỉ cần thêm 9 điểm nữa, thầy trò Pep Guardiola sẽ chính thức có mặt ở vòng 16 đội.\r\n\r\nDường như, nhà vô địch đang ung dung tiến bước trên con đường chinh phục Champions League và với một Haaland xuất sắc như hiện tại, người hâm mộ hoàn toàn có quyền mơ về những vinh quang lớn hơn.', 'Haaland2_2_', 1, 1, 100, '2024-10-24 07:25:58', '0000-00-00 00:00:00', 1),
(1, 'Apple muốn mở cửa hàng game', 'Nhiều nguồn tin nội bộ tiết lộ Apple đang xây dựng một ứng dụng kết hợp App Store và Game Center dành riêng cho mảng game.', 'Công nghệ\r\n\r\nApple muốn mở cửa hàng game\r\nAnh Tuấn Thứ tư, 23/10/2024 16:14 (GMT+7)Nhiều nguồn tin nội bộ tiết lộ Apple đang xây dựng một ứng dụng kết hợp App Store và Game Center dành riêng cho mảng game.\r\n\r\n\r\n<img scr=\"https://photo.znews.vn/w960/Uploaded/ivpbciv2/2024_10_23/tai_xuong_2024_10_23T110922.297.jpeg\">\r\nMột kho ứng dụng hoàn toàn mới dành riêng cho game di động của Apple đang được phát triển. Ảnh: Apple.\r\nSự quan tâm của Apple đối với mảng trò chơi ngày càng rõ nét. Mới đây, 9to5Mac tiết lộ Táo khuyết đang phát triển một \"ứng dụng giống như App Store dành riêng cho game\". Cụ thể, trích dẫn từ các nguồn đáng tin cậy quen thuộc với kế hoạch của Apple, 9to5Mac đưa tin hãng đang xây dựng một ứng dụng kết hợp chức năng của App Store và Game Center, nhưng tập trung chặt chẽ vào trò chơi di động.\r\n\r\nNguồn tin cũng lưu ý app mới này sẽ không thay thế Game Center - ứng dụng theo dõi tiến trình chơi game và kết nối bạn bè dành cho người dùng từ iOS 14. Thay vào đó, kho tài nguyên sẽ tích hợp hồ sơ Game Center của người dùng. Các nguồn tin khẳng định ứng dụng chưa được đặt tên này sẽ có nhiều tab, tương tự như App Store.\r\n\r\nMột trong số đó là tab \"Play Now\" sẽ hiển thị cho người dùng trò chơi, bạn bè và nhiều thứ khác. Người dùng cũng sẽ thấy nội dung biên tập từ Apple và các gợi ý về game nào sẽ tải xuống tiếp theo, cùng với các thử thách, bảng xếp hạng và thành tích.\r\n\r\nNgoài ra, kho cũng chứa trò chơi từ App Store cũng như dịch vụ Arcade. Các sự kiện đặc biệt và thông tin về các bản cập nhật trong game được ứng dụng làm nổi bật để thông báo đến người dùng.\r\n\r\nThêm vào đó, Apple cũng đang thử nghiệm tích hợp FaceTime và iMessage để giúp người dùng có thể giao lưu nhiều hơn trong lúc chơi game, bên cạnh việc cho phép các nhà phát triển tạo các game nhỏ bằng App Clips không yêu cầu phải tải xuống.', 'tai_xuong_2024_10_23T110922.297', 6, 1, 100, '2024-10-24 07:59:33', '2024-10-24 07:59:33', 2),
(2, 'Hệ lụy khi phông bạt con số 78.000 khán giả ở concert Anh trai say hi', 'Chuyên gia nhận định việc nhà sản xuất công bố thông tin 78.000 khán giả xem concert Anh trai \'say hi\' trong hai đêm nhưng không có bằng chứng sẽ gây ra nhiều hệ lụy.', '\"78.000\" trở thành một từ khóa gây bùng nổ và chiếm sóng mạng xã hội trong vài ngày qua. Thậm chí, con số này được biến thể thành nhiều meme nhằm mục đích gây cười và cả chế giễu trên các nền tảng như Facebook, TikTok, Threads.\r\n\r\nNguồn cơn xuất phát từ sự việc nhà sản xuất công bố thông tin có 78.000 khán giả trực tiếp theo dõi hai đêm concert Anh trai \"say hi\" tại TP.HCM. Song ngay sau đó, một luồng phản ứng dữ dội từ cộng đồng mạng nổ ra.\r\n\r\nChỉ vài phép tính nhẩm đơn giản, công chúng nhận ra con số 78.000 khán giả do nhà sản xuất cung cấp là điều bất khả thi, khó tin. Không chỉ vậy, trong quá trình diễn ra concert thứ hai, MC Trấn Thành lẫn các anh trai tham gia đều đưa ra những con số khác nhau về số liệu người xem trực tiếp, tạo ra sự hỗn loạn về thông tin.\r\n\r\nTuy nhiên, đến thời điểm hiện tại, nhà sản xuất Anh trai \"say hi\" vẫn nhất quyết giữ động thái im lặng.\r\n\r\nSự minh bạch là cần thiết\r\nTrao đổi với Tri Thức - Znews, ông Hoàng Quang Lê, CEO Techbeat Records nhận định việc nhà sản xuất công bố con số 78.000 khán giả tham dự trực tiếp hai đêm concert Anh trai \"say hi\" ở 2 địa điểm Công viên bờ sông Sài Gòn và đô thị Vạn Phúc City (TP.HCM) xuất phát từ chiêu truyền thông, mục đích thu hút sự chú ý của công chúng và nâng cao tầm ảnh hưởng cho chương trình.\r\n\r\nCon số nói trên là quá lớn đối với một sự kiện có bán vé tại Việt Nam, đặc biệt với quy mô và bối cảnh hiện tại của thị trường âm nhạc nội địa.\r\n\r\n\r\n\r\n\r\n\r\n<img scr=\"https://photo.znews.vn/w960/Uploaded/qfssu/2024_10_20/Toan_canh_Dem_2_1_.jpg\">\r\nDàn nghệ sĩ trẻ trong đêm concert thứ hai của Anh trai \"say hi\".\r\n\"Việc công bố số lượng 78.000 khán giả như vậy có thể mang lại nhiều lợi ích: tạo sức ép cạnh tranh với các sự kiện khác, gây ấn tượng với khán giả, và đặc biệt là thu hút nhà tài trợ khi họ luôn quan tâm đến quy mô và sức ảnh hưởng của sự kiện\", ông Hoàng Quang Lê nói.\r\n\r\nSong trên thực tế, con số này không đi kèm với minh chứng cụ thể, thậm chí có phần mập mờ, nhiễu loạn thông tin, tạo ra một \"hiệu ứng ngược\", dẫn đến sự quay lưng, phản ứng từ phía khán giả đối với nhà sản xuất những ngày qua.\r\n\r\n\"Trong ngành công nghiệp giải trí hiện nay, sự minh bạch và niềm tin của khán giả là yếu tố then chốt. Nếu thông tin được chứng minh là không chính xác hoặc bị xem là ‘phông bạt’, nhà sản xuất có thể đối mặt với những hệ lụy nghiêm trọng về uy tín, không chỉ ảnh hưởng đến sự kiện mà còn đến cả các nghệ sĩ tham gia\", chuyên gia nói.\r\n\r\nTheo ông Hoàng Quang Lê việc thổi phồng số liệu để tạo hiệu ứng truyền thông không quá xa lạ trong giới giải trí. Tuy nhiên, trong bối cảnh hiện nay, khi mà khán giả ngày càng thông minh và dễ dàng tiếp cận với thông tin, đây là một con dao hai lưỡi. Nếu con số này được đưa ra mà không có cơ sở xác thực, nhà sản xuất sẽ phải đối mặt với sự phản ứng dữ dội từ công chúng, và thậm chí là có nguy cơ mất hết uy tín.\r\n\r\nCùng quan điểm, ông Tất Hữu Đăng Khoa, cựu Giám đốc âm nhạc của TikTok Vietnam, nhận định thời điểm diễn ra concert thứ hai của Anh trai \"say hi\" cũng là lúc đối thủ cạnh tranh là concert Anh trai vượt ngàn chông gai mở màn. Do đó, áp lực về truyền thông, hiệu ứng social đối với đơn vị tổ chức không hề nhỏ.\r\n\r\nChuyên gia nhận định trong bối cảnh kể trên, nhiều yếu tố tác động vào việc công bố số lượng khán giả cụ thể từ phía nhà sản xuất, ví dụ từ số tổng người xem có thể ước tính lượng vé và tổng doanh thu qua đó sẽ ảnh hưởng đến một số vấn đề về thu nhập, thuế. Ngoài ra diện tích địa điểm cũng là một thông số có thể tính được lượng khán giả nếu vượt hơn, lại ảnh hưởng đến an toàn đêm diễn.\r\n\r\nTrong vài trường hợp có thể xảy ra sự nhầm lẫn trong quá trình truyền thông nội bộ, dẫn tới sai sót khi đưa ra con số cuối cùng. Song thay vì im lặng, nhà sản xuất phải lên tiếng giải thích, phân tích hợp lý cho công chúng.\r\n\r\n\"Nếu đây là chiêu trò truyền thông của nhà sản xuất là sai lầm tệ hại và phải chịu trách nhiệm. Bởi trên thực tế, với uy tín và quy mô của nhà sản xuất này, họ không cần thiết phải làm thế. Tôi vẫn hy vọng nhà sản xuất có phản hồi sớm về số liệu trên kèm theo dòng chia sẻ \'Cảm ơn sự quan tâm và đón nhận của khán giả\", ông Tất Hữu Đăng Khoa trao đổi.\r\n\r\nHệ lụy của \'phông bạt\' số liệu người xem\r\nNếu phát ngôn \"Mẹ thấy chưa, mẹ thấy đúng khi cho con nghỉ học chưa\", của Negav làm lu mờ concert đầu tiên của Anh trai \"say hi\" hôm 28/9, sự việc nhà sản xuất \"phông bạt\" số liệu 78.000 khán giả xem trực tiếp hai đêm cũng hệ lụy tương tự. Ồn ào này gần như đạp đổ những công sức, hình ảnh của dàn nam nghệ sĩ trẻ sau concert thứ hai.\r\n\r\nTheo CEO Techbeat Records, việc nhà sản xuất mất uy tín, niềm tin đối với khán giả lẫn nhà tài trợ có thể liên lụy tới những dự án sau này. Đối với các nghệ sĩ tham gia show, họ cũng có thể bị ảnh hưởng gián tiếp. Bởi hình ảnh của dàn anh trai gắn liền với chương trình. Sự mập mờ về số liệu không chỉ làm giảm giá trị của chương trình mà còn có thể gây ảnh hưởng tiêu cực đến các nghệ sĩ, đặc biệt là khi họ có thể bị cuốn vào các tranh cãi không đáng có.\r\n\r\n\r\n<img src=\"https://photo.znews.vn/w1920/Uploaded/wpdhnwhnw/2024_10_23/op.jpg\">\r\nNhà sản xuất Anh trai \"say hi\" công bố con số 78.000 khán giả trực tiếp theo dõi hai đêm concert, dẫn đến bị phản ứng.\r\n\"Để đối phó với sự chỉ trích và thắc mắc từ khán giả, nhà sản xuất nên nhanh chóng có một thông báo chính thức, giải thích rõ ràng về số lượng khán giả tham dự. Nếu con số 78.000 bao gồm cả lượng người theo dõi qua livestream hay các kênh khác, điều này cần được minh bạch. Bất kỳ sự trì hoãn nào trong việc đưa ra thông báo sẽ chỉ khiến tình hình thêm căng thẳng và mất kiểm soát. Sự minh bạch là chìa khóa để giữ lòng tin của công chúng và duy trì uy tín\", ông Hoàng Quang Lê nhận định.\r\n\r\nTheo chuyên gia, vụ việc vừa qua là bài học lớn cho các nhà sản xuất, đơn vị tổ chức concert. Trong đó, sự chính xác ở khâu tính toán và công bố số liệu là tối quan trọng để tránh những tranh cãi không đáng có.\r\n\r\n\"Thông thường, số lượng khán giả tham gia các concert được tính dựa trên số lượng vé bán ra hoặc lượng người vào cửa, và trong một số trường hợp có thể bao gồm cả người xem qua livestream. Tuy nhiên, các con số này phải được kiểm chứng rõ ràng và minh bạch. Ở Việt Nam, chúng ta vẫn đang trong quá trình phát triển các hệ thống quản lý sự kiện theo tiêu chuẩn quốc tế. Việc nhà sản xuất cố gắng tạo hiệu ứng bằng cách thổi phồng thông tin, số liệu chưa bao giờ là hướng đi bền vững và đáng tin cậy trong dài hạn\", ông kết luận.\r\n\r\nCuốn sách đáng đọc về âm nhạc\r\n\r\nSách khảo cứu Lịch sử opera Ý do GS Gianni Kriscak cùng Hiền Nguyễn Soprano (nghiên cứu sinh tại Đại học Sư phạm Nghệ thuật Trung Ương), Trịnh Thị Oanh (giảng viên thanh nhạc tại Đại học Sư phạm Nghệ thuật Trung Ương) thực hiện. Sách ra mắt vào tháng 2 và nhanh chóng được bạn đọc đón nhận.\r\n\r\nSách khảo cứu Lịch sử opera Ý là tác phẩm hiếm hoi về opera được thực hiện bằng tiếng Việt. Qua cuốn sách này, các tác giả đưa ra câu chuyện về một thành tựu văn hóa kinh điển của thế giới nhưng bằng góc nhìn nhẹ nhàng, dễ tiếp cận thay vì quá nặng nề, phức tạp. Các tác giả không đề cập quá nhiều tên, chi tiết và chỉ đưa vào cuốn sách những khoảnh khắc quan trọng, thú vị nhất trong giai đoạn đầu tiên của các vở opera.', 'op', 5, 1, 100, '2024-10-24 08:03:42', '2024-10-24 08:04:33', 3),
(3, 'Chuyện gì đang xảy ra với người giàu nhất thế giới?', 'Số tiền khổng lồ và nỗ lực vận động tranh cử giúp ông Donald Trump của người giàu nhất hành tinh khiến giới quan sát hoài nghi về động lực đằng sau nước cờ chính trị này.', 'Số tiền khổng lồ và nỗ lực vận động tranh cử giúp ông Donald Trump của người giàu nhất hành tinh khiến giới quan sát hoài nghi về động lực đằng sau nước cờ chính trị này.\r\n\r\n\r\n\r\nTỷ phú Elon Musk hôm 5/10 xuất hiện tại buổi vận động tranh cử của ông Trump ở Butler, Pennsylvania. Ông chủ Tesla đã phấn khích bật nhảy tới 2 lần trong sự kiện. Ảnh: New York Times.\r\nTỷ phú Elon Musk gần đây cam kết sẽ trao tặng 1 triệu USD mỗi ngày cho những cử tri tại các bang chiến địa đã ký vào bản kiến nghị cam kết ủng hộ Tu chính án thứ nhất và thứ hai.\r\n\r\nNgười giàu nhất hành tinh đã trao tấm séc đầu tiên tại một cuộc mít tinh ở Pennsylvania vào ngày 19/10 và tấm séc thứ hai tại Pittsburgh hôm 20/10, theo Guardian.\r\n\r\nÔng Musk tuyên bố sẽ tiếp tục trao tặng những giải thưởng này cho đến ngày bầu cử 5/11. Tuy nhiên, giới chuyên gia cho rằng hành động này có thể bị quy là vi phạm luật liên bang.\r\n\r\nSau khi tuyên bố ủng hộ ứng viên tổng thống đảng Cộng hòa Donald Trump vào tháng 7, tỷ phú gốc Nam Phi đã thành lập một uỷ ban hành động chính trị và chi 75 triệu USD cho các hoạt động của tổ chức này.\r\n\r\nTrong vài tuần qua, ông Musk đã nhiều lần xuất hiện trực tiếp tại các sự kiện vận động tranh cử, đặc biệt tập trung vào bang chiến trường Pennsylvania.\r\n\r\n\r\n<img src=\"https://photo.znews.vn/w1920/Uploaded/liqpwivo/2024_10_23/Screen_Shot_2024_10_23_at_23.30.46.jpg\">\r\nHai ông Donald Trump và Elon Musk nuôi tham vọng thành lập Cục Kiểm soát Hiệu suất Chính phủ. Ảnh: X.\r\nHai cây bút chính trị lão luyện là Nick Robins-Early và Rachel Leingang lý giải rằng việc tỷ phú Musk tham gia sâu vào cuộc bầu cử Mỹ là chuyện khó tránh.\r\n\r\nNgười giàu nhất thế giới đã nhiều lần công khai tuyên bố ủng hộ việc cắt giảm các lớp quy định quan liêu của chính phủ Mỹ. Ý tưởng này được ông Trump ủng hộ và từng tuyên bố vào tháng 9 rằng nếu đắc cử, ông sẽ thành lập một ủy ban cải thiện hiệu quả chính phủ và chỉ định ông Musk đứng đầu.\r\n\r\nMặc dù kế hoạch nói trên vẫn còn nhiều điểm chưa rõ ràng song cả ông Trump lẫn ông Musk đều nhiều lần nêu ý tưởng rằng tỷ phú gốc Nam Phi sẽ nắm giữ một vai trò nhất định trong chính quyền đảng Cộng hoà nếu phe này chiến thắng trong cuộc bầu cử.\r\n\r\nTrong một lần xuất hiện trên Fox News vào đầu tuần này, cựu Tổng thống Trump cho biết ông sẽ tạo ra một vị trí mới có tên là \"bộ trưởng cắt giảm chi phí\" và bổ nhiệm ông Musk.\r\n\r\nKhi những đồn đoán về việc ông Trump sẽ trao cho ông Musk vị trí lãnh đạo trong ủy ban kiểm soát hiệu suất chính phủ trong tương lai, ông Musk đã đăng trên mạng xã hội X: \"Tôi mong muốn được phục vụ nước Mỹ nếu có cơ hội, không cần lương, không cần chức danh, không cần sự công nhận\".\r\n\r\nTuy nhiên, ông Musk đang nỗ lực cắt giảm bớt các cơ quan và thủ tục hành chính không chỉ trong phạm vi nước Mỹ. Đôi khi, cuộc chiến của ông với \"các cơ quan quản lý\" khiến ông phải đối đầu với những tỷ phú khác.\r\n\r\nỞ Ấn Độ, ông Musk phải đấu tranh với chính phủ nước này về việc phân phối băng thông vệ tinh dải rộng và giành chiến thắng trước tỷ phú Mukesh Ambani. Người đàn ông giàu nhất châu Á đã tìm kiếm các điều khoản có lợi hơn cho đế chế viễn thông của riêng mình.\r\n\r\n\r\n<img src=\"https://photo.znews.vn/w1920/Uploaded/liqpwivo/2024_10_23/Screen_Shot_2024_10_23_at_23.34.29.jpg\">\r\nĐộng lực thúc đẩy người giàu nhất hành tinh dấn sâu vào chính trường được cho là nỗ lực cắt giảm các thủ tục hành chính và cơ quan chính phủ, giúp ông dễ hoạt động hơn. Ảnh: ABC News.\r\nÔng Musk cũng từng tự nhận bản thân là \"người theo chủ nghĩa tuyệt đối về tự do ngôn luận\" và nhiều lần lên tiếng phàn nàn về các cơ quan quản lý ngôn luận, theo Guardian.\r\n\r\nKhi Vương quốc Anh đang trong cơn khủng hoảng về mâu thuẫn sắc tộc dữ dội một tháng sau cuộc tổng tuyển cử, Musk đã đănd trên mạng xã hội X: \"Nội chiến là điều không thể tránh khỏi\".\r\n\r\nNhư châm dầu vào lửa, ông Musk thậm chí đăng bức biếm họa có hình một nhân vật ngồi trên ghế điện và tuyên bố rằng đây sẽ là hình phạt của chính phủ đối với quyền tự do ngôn luận tại Vương quốc Anh vào năm 2030.\r\n\r\nÔng Musk cũng từng đưa ra những lời chỉ trích tương tự đối với chính quyền California và chính quyền Tổng thống Joe Biden, theo Guardian.\r\n\r\nCuộc chiến cắt giảm cơ quan và thủ tục hành chính thường xuyên khiến ông Musk phải đối đầu với ngành tư pháp ở bất kỳ quốc gia nào mà ông hoạt động.\r\n\r\nVào tháng 9, Brazil đã chặn công dân nước này truy cập vào mạng xã hội X vì công ty này không tuân thủ lệnh của thẩm phán và sau đó phạt Starlink, một công ty con của SpaceX. Ông Musk và X cuối cùng đã chấp nhận hình phạt trên.\r\n\r\n\r\n\r\nCuộc chiến \"cắt giảm\" của ông Musk đã khiến X và các công ty dưới quyền quản lý của ông chịu nhiều thử thách. Ảnh: Unsplash.\r\nGần đây, một số cơ quan quản lý đã thực hiện một chiến thuật mới: họ bắt đầu trừng phạt một công ty do ông Musk đứng đầu vì hành vi vi phạm của một công ty khác thuộc quản lý của tỷ phú này.\r\n\r\nVào giữa tháng 10, các cơ quan quản lý ở châu Âu đã nối gót Brazil bằng cách liên lạc với các luật sư tại X rằng Liên minh châu Âu (EU) có thể đánh thuế đối với công ty truyền thông xã hội này vì không tuân thủ Đạo luật Dịch vụ Kỹ thuật số.\r\n\r\nĐiểm mấu chốt nằm ở chỗ các cơ quan quản lý đề nghị tính toán thuế không chỉ dựa trên doanh thu của X, mà là tổng doanh thu của các doanh nghiệp thuộc quản lý của ông Musk.\r\n\r\nTại California, một ủy ban đã chỉ ra hành vi đăng tải thông tin sai lệch của ông Musk trên X để từ chối một bản kiến ​​nghị của SpaceX và Không quân Mỹ nhằm phóng thêm tên lửa từ căn cứ trên bãi biển Santa Barbara. Ông Musk đã kiện để đáp lại, cáo buộc cơ quan này thiên vị chính trị và vi phạm Tu chính án thứ nhất.', 'Screen_Shot_2024_10_23_at_23.34.29\r\nScreen_Shot_2024_10_23_at_23.30.46', 3, 1, 100, '2024-10-24 08:10:02', '2024-10-24 08:10:02', 4),
(4, 'Thủ tướng gặp Tổng Bí thư, Chủ tịch Trung Quốc Tập Cận Bình', 'Ngày 23/10, tại Nga, Thủ tướng Phạm Minh Chính đã có cuộc gặp ngắn với Tổng Bí thư BCH TW Đảng Cộng sản Trung Quốc, Chủ tịch nước Cộng hòa nhân dân Trung Hoa Tập Cận Bình.', 'Ngày 23/10, tại Nga, Thủ tướng Phạm Minh Chính đã có cuộc gặp ngắn với Tổng Bí thư BCH TW Đảng Cộng sản Trung Quốc, Chủ tịch nước Cộng hòa nhân dân Trung Hoa Tập Cận Bình.\r\n\r\n\r\n<img src=\"https://photo.znews.vn/w660/Uploaded/sgtnrn/2024_06_21/A1_2.jpg>\r\nThủ tướng Chính phủ Phạm Minh Chính gặp Tổng Bí thư Ban Chấp hành Trung ương Đảng Cộng sản Trung Quốc, Chủ tịch nước Cộng hòa nhân dân Trung Hoa Tập Cận Bình. Ảnh: VGP.\r\nTại cuộc gặp, Thủ tướng Chính phủ Phạm Minh Chính trân trọng chuyển lời thăm hỏi chân thành của Tổng Bí thư Tô Lâm, Chủ tịch nước Lương Cường, Chủ tịch Quốc hội Trần Thanh Mẫn tới Tổng Bí thư, Chủ tịch nước Tập Cận Bình và các đồng chí lãnh đạo chủ chốt Trung Quốc; chúc mừng những thành tựu to lớn, mang ý nghĩa lịch sử trong công cuộc xây dựng đất nước 75 năm qua của Đảng, Nhà nước, nhân dân Trung Quốc; khẳng định Việt Nam luôn coi phát triển quan hệ với Trung Quốc là chủ trương nhất quán, yêu cầu khách quan, lựa chọn chiến lược, ưu tiên hàng đầu trong đường lối đối ngoại của Việt Nam.\r\n\r\nThủ tướng Chính phủ Phạm Minh Chính hoan nghênh những đóng góp nổi bật của Trung Quốc trong cơ chế BRICS, bày tỏ sẵn sàng phối hợp tích cực với các quốc gia thành viên BRICS thúc đẩy hơn nữa vai trò của các nước đang phát triển trong xử lý các vấn đề hòa bình và phát triển toàn cầu.\r\n\r\nVề quan hệ song phương Việt Nam - Trung Quốc, Thủ tướng Phạm Minh Chính đánh giá cao quan hệ hai Đảng, hai nước thời gian qua không ngừng được củng cố và phát triển, bước sang giai đoạn phát triển mới với mục tiêu xây dựng Cộng đồng chia sẻ tương lai Việt Nam - Trung Quốc có ý nghĩa chiến lược theo định hướng \"6 hơn\". Thủ tướng đề nghị hai bên tiếp tục tăng cường trao đổi, tiếp xúc cấp cao; thúc đẩy hợp tác thực chất trên các lĩnh vực đi vào chiều sâu, trong đó có kết nối giao thông, nhất là ưu tiên đẩy nhanh triển khai 3 tuyến đường sắt khổ tiêu chuẩn kết nối Việt Nam với Trung Quốc (Lào Cai - Hà Nội - Hải Phòng, Lạng Sơn - Hà Nội, Móng Cái - Hạ Long - Hải Phòng) nhằm thúc đẩy kết nối hai nền kinh tế; phối hợp tổ chức tốt các hoạt động kỷ niệm 75 năm thiết lập quan hệ ngoại giao hai nước và Năm Giao lưu nhân văn Việt - Trung 2025.\r\n\r\nTổng Bí thư, Chủ tịch Trung Quốc Tập Cận Bình gửi lời cảm ơn và thăm hỏi chân thành tới Tổng Bí thư Tô Lâm và các đồng chí lãnh đạo chủ chốt Việt Nam; chúc mừng đồng chí Lương Cường vừa được bầu giữ chức Chủ tịch nước; chúc mừng những thành tựu Việt Nam đã đạt được trên tất cả các mặt, nhất là kinh tế - xã hội.\r\n\r\nTổng Bí thư, Chủ tịch Tập Cận Bình bày tỏ vui mừng trước xu thế phát triển, ngày càng thực chất, hiệu quả của quan hệ Trung-Việt; nhấn mạnh trong bất cứ hoàn cảnh nào cũng cần thúc đẩy quan hệ theo tinh thần \"4 tốt\" và những nhận thức chung cấp cao đã đạt được; sẵn sàng cùng Việt Nam duy trì trao đổi chiến lược, thúc đẩy làm sâu sắc hơn nữa hợp tác thực chất, đưa quan hệ Đối tác hợp tác chiến lược toàn diện, Cộng đồng chia sẻ tương lai Trung - Việt có ý nghĩa chiến lược không ngừng đi vào chiều sâu. Để thúc đẩy kết nối giữa hai nền kinh tế, Tổng Bí thư, Chủ tịch Trung Quốc Tập Cận Bình cho biết sẽ chỉ đạo các cơ quan liên quan Trung Quốc thúc đẩy kết nối giao thông Trung - Việt.', 'A1_2', 7, 1, 100, '2024-10-24 08:12:26', '2024-10-24 08:12:26', 5),
(5, 'Giá xăng giảm', 'Từ 15h ngày 24/10, giá xăng RON 95 giảm xuống 20.890 đồng/lít. Từ đầu năm đến nay, mặt hàng này đã có 20 lần tăng giá và 23 lần giảm giá.', 'Từ 15h ngày 24/10, giá xăng RON 95 giảm xuống 20.890 đồng/lít. Từ đầu năm đến nay, mặt hàng này đã có 20 lần tăng giá và 23 lần giảm giá.\r\n\r\n\r\n<img src=\"https://photo.znews.vn/w960/Uploaded/rohuoct/2024_07_24/vietlinh6_znews.jpg\">\r\nGiá xăng RON 95 giảm còn 20.890 đồng/lít. Ảnh: Việt Linh.\r\nTheo kế hoạch, liên Bộ Công Thương - Tài chính đã điều chỉnh giá bán lẻ xăng dầu theo chu kỳ vào chiều nay (24/10).\r\n\r\nTrong đó, cơ quan điều hành quyết định giảm 40 đồng/lít với xăng E5 RON 92 và giảm 70 đồng trên mỗi lít xăng RON 95. Sau điều chỉnh, giá bán lẻ tối đa với mặt hàng xăng E5 RON 92 là 19.690 đồng/lít và xăng RON 95 là 20.890 đồng/lít.\r\n\r\nNhư vậy, giá nhiên liệu trong nước đã quay đầu giảm 2 phiên liên tiếp. Từ đầu năm đến nay, giá xăng RON 95 đã có 20 lần tăng và 23 lần giảm.\r\n\r\nTương tự, giá dầu cũng được điều chỉnh giảm trong kỳ điều hành này. Cụ thể, giá dầu diesel giảm 270 đồng xuống 18.050 đồng/lít. Hiện loại nhiên liệu này đã có 18 lần tăng và 23 lần giảm kể từ đầu năm.\r\n\r\nTrước đó, lãnh đạo một số doanh nghiệp xăng dầu dự báo giá xăng có khả năng giảm trở lại trong kỳ điều hành hôm nay. Hiện chiết khấu xăng dầu ở nhiều kho đang dao động quanh mức 900-1.450 đồng/lít.\r\n\r\nTại kỳ điều hành này, liên Bộ vẫn duy trì không trích hay chi Quỹ bình ổn giá. Số liệu từ Bộ Tài chính cho thấy số dư Quỹ bình ổn giá xăng dầu tính đến cuối quý II là 6.061 tỷ đồng, chỉ giảm 18 tỷ đồng so với quý liền trước.\r\n\r\nTrong đó, số dư tại Tập đoàn Xăng dầu Việt Nam (Petrolimex) chiếm hơn một nửa, ở mức gần 3.079 tỷ đồng. Một số doanh nghiệp đầu mối, phân phối xăng dầu cũng ghi nhận số dư Quỹ bình ổn giá ở mức dương lớn như Công ty TNHH MTV dầu khí TP.HCM (Saigon Petro) là 328 tỷ đồng; Công ty TNHH MTV Tổng công ty xăng dầu Quân đội (Mipec) là gần 300 tỷ đồng; Công ty CP Thương mại dầu khí Đồng Tháp dương 460 tỷ đồng.', 'vietlinh6_znews', 4, 1, 100, '2024-10-24 08:18:04', '2024-10-24 08:18:39', 6);

